	(979, 'Culture Department', NULL, 4276, 'Assistance for Rare Styles & Instruments of Music', 'The scheme seeks to boost art and culture as well as entertainment industry, which will boost the economy by creating various opportunities.'),
	(980, 'Culture Department', NULL, 4283, 'Museum', 'The scheme is to boost museum'),
	(981, 'Culture Department', NULL, 444, 'Promotion of Modern Indian Languages and Literature', 'The scheme aims to promote modern Indian languages and literature'),
