	(10021, 'SIF_TARGET104', '1.4.7b Proportion of households having access to toilet facility (Urban) (in percentage) (similar to 6.2.1)', 100.000000000000000000000000000000, 0.000000000000000000000000000000, 'National Family Health Survey (NFHS), Ministry of Health and Family Welfare, GoI', 'Percentage', 'yearly', 'N', 82.400000000000000000000000000000, 31, 21, 'Numerator: Total households having acces s to toilet facility\nDenominator: Mid-year total households of the reference year Multiplier: 100', 'NSS 76th Round, Drinking water, Sanitation and Housing, MoSPI, GoI', 'http://rchiips.org/nfhs//http://mospi.nic.in/unit-level-data-report-nss-76th-round-schedule-12-july-\ndecember- 2018-drinking-water-sanitation'),
	(10027, 'SIF_TARGET108', '2.1.1 Children under 5 years who are underweight (weight-for-age) (%)', 0.900000000000000000000000000000, 1.000000000000000000000000000000, 'National Family Health Survey-4, Ministry of Health and Family Welfare, GoI', 'Percentage', 'yearly', 'N', 37.700000000000000000000000000000, 32, NULL, 'This indicator is defined as the percentage of children under 5 years of age (i.e. 0 - 59 months) whose weight-for-age is below minus 2 times standard deviations of the WHO Child Growth Standards median.\nNumerator: Children aged 0-59 months who are underweight Denominator: Number of children in age group 0 -59 months Multiplier: 100', 'Comprehensive National Nutrition Survey (CNN), Mi nistry of Health and Family\nWelfare, GoI', 'http://rchiips.org/nfhs///https://www.popcouncil.org/uploads/pdfs/2019RH_CNNSreport.pdf'),
	(10287, 'SIF_TARGET194', '11.6.5 Percentage of waste processed', 100.000000000000000000000000000000, 0.000000000000000000000000000000, 'SBM(U)-MIS, Swacchh Sarvekhsan ', 'Percentage', 'yearly', 'N', 2.000000000000000000000000000000, 31, 21, 'Numerator: Total waste processed during reference year\nDenominator: Total waste generation during the reference year Multiplier: 100', 'SBM(U)-MIS, Swacchh Sarvekhsan ', 'http://www.swachhbharaturban.gov.in/'),
	(10030, 'SIF_TARGET109', '2.2.2 Children under 5 years who are wasted (weight-for-height) (%)', 2.000000000000000000000000000000, 1.000000000000000000000000000000, 'National Family Health Survey-4, Ministry of Health and Family Welfare, GoI', 'Percentage', 'yearly', 'N', 23.097480000000000000000000000000, 32, NULL, 'This indicator is defined as percentage of Children aged 0 -59 months who are wasted (children whose weight-for-height is below minus - 2 times standard deviation of the WHO Child Growth Standards median).\n\nNumerator: Number of wasted children aged 0 -59\nDenominator: Number of children aged 0 -59 months whose weight is measured Multiplier: 100', 'Comprehensive National Nutrition Survey (CNN), Ministry of Health and Family Welfare, GoI', 'http://rchiips.org/nfhs/ //https://www.popcouncil.org/uploads/pdfs/2019RH_CNNSreport.pdf'),
	(10031, 'SIF_TARGET109', '2.2.3 Percentage of women (aged 15-49 years) whose Body Mass Index is below 18.5  ', 15.000000000000000000000000000000, 1.000000000000000000000000000000, 'National Family Health Survey-4, Ministry of Health and Family Welfare, GoI', 'Percentage', 'yearly', 'N', 26.700000000000000000000000000000, 20, 10, 'Numerator: Number of women aged 15-49 years with BMI < 18.5\nDenominator: Total number of women aged 15-49years measured                                  Multiplier: 100 ', 'National Family Health Survey, Ministry of Health and Family Welfare, GoI', 'http://rchiips.org/nfhs/'),
	(10032, 'SIF_TARGET109', '2.2.4 Pregnant women age 15-49 years who are anaemic (<11.0 g/dl) (%)', 25.150000000000000000000000000000, 1.000000000000000000000000000000, 'National Family Health Survey-4, Ministry of Health and Family Welfare, GoI', 'Percentage', 'yearly', 'N', 41.500000000000000000000000000000, 20, 10, 'This indicator is defined as percentage of pregnant women age 15 -49 years who are anaemic (Hb<11.0 g/dl)\nNumerator: Number of pregnant women aged 15 -49 years who are anaemic Denominator: Number of pregnant women aged 15 -49 years whose Hb level is measured\nMultiplier: 100\nNote: g/dl refer as gram per deciliter', 'National Family Health Survey-4, Ministry of Health and Family Welfare, GoI', 'http://rchiips.org/nfhs/'),
	(10024, 'SIF_TARGET105', '1.5.2 Proportion of district that adopt and implement local disaster risk reduction strategies in line with national disaster reduction strategies, (similar to 11.b.2) (in %)', 100.000000000000000000000000000000, 0.000000000000000000000000000000, 'Revenue & Disaster Management, Government of Chhattisgarh', 'Percentage', 'yearly', 'N', 10.710000000000000000000000000000, 28, NULL, 'This indicator is defined as the number of districts that adopt and implement local disaster risk reduction strategies in line with national disaster reduction strategies among all the districts in the state.\nNumerator: Number of districts that adopt and implement local disaster risk reduction strategies in line with national disaster reduction strategies.\nDenominator: Total number of districts in India Multiplier: 100\nThe National Disaster Management Plan (NDMP) is based on th e four priority themes of the Sendai Framework, namely: understanding disaster risk, improving disaster risk governance, investing in disaster reduction (through structural and non -structural measures)and disaster preparedness, early warning and building back better in the\naftermath of a disaster.', 'Revenue & Disaster Management, Government of Chhattisgarh', 'http://sdma.cg.gov.in/'),
	(10028, 'SIF_TARGET108', '2.1.2 Proportion of beneficiaries covered under Chhattisgarh Food Security Act 2012, (in percentage)', 100.000000000000000000000000000000, 0.000000000000000000000000000000, 'NIC and Department Database, Department of Food and Civil Supplies, Government of Chhattisgarh', 'Percentage', 'yearly', 'N', 100.000000000000000000000000000000, 17, NULL, 'Numerator: Total number of beneficiaries covered under Chhattisgarh Food Security Act 2012\nDenominator: Total state population. Multiplier: 100', 'NIC and Department Database, Department of Food and Civil Supplies, Government of Chhattisgarh', 'NA'),
